import React, { useState, useEffect } from "react";
import List from "./components/List"
import WithLoadingList from "./components/WithLoadingList";
import './App.css'
import { deleteFetch, getFetch, postFetch, putFetch } from "./components/FetchMethods";

function App() {
  const LoadingList = WithLoadingList(List);

  const [appStateLoading, setAppStateLoading] = useState(false)
  const [appStateObject, setAppStateObject] = useState(null)

  const [name, setName] = useState("");
  const [year, setYear] = useState(null);
  const [director, setDirector] = useState(null);
  const [message1, setMessage1] = useState("");


  const [refresh, setRefresh] = useState(true);

//  const [nameEdit, setNameEdit] = useState("");
//  const [idEdit, setIdEdit] = useState("");
//  const [message2, setMessage2] = useState("");

//  const [idDelete, setIdDelete] = useState("");
//  const [message3, setMessage3] = useState("");

  useEffect(() => {
    if (refresh) {
      setAppStateLoading(true);
      getFetch("movies").then(val => setAppStateObject(val))
      setAppStateLoading(false);
      setRefresh(false);
    }

  }, [setAppStateObject, setAppStateLoading, refresh])

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      postFetch("movies", {name: name, year: year, director: director}).then(() => {
        setName("");
        setYear(null);
        setDirector(null);
        setMessage1("La película se creó correctamente");
        setRefresh(true);
      });
    } catch (err) {
      console.log(err)
    }
  }
/*
  const handleEdit = async (e) => {
    e.preventDefault();
    try {
      putFetch(`medicines/${idEdit}`, {name: nameEdit}).then(() => {
        setNameEdit("");
        setIdEdit("");
        setMessage2("Editado correctamente");
        setRefresh(true);
      })
    } catch (err) {
      console.log(err)
    }
  }

  //arrow function
  const handleDelete = async (e) => {
    e.preventDefault();
    try {
      deleteFetch(`medicines/${idDelete}`).then((e) => {
        setIdDelete("");
        setRefresh(true);
        setMessage3("Eliminado correctamente");
      })
    } catch (err) {
      console.log(err);
    }
  }**/

  return (
    <div class="App">
      <div style={{borderStyle: "dashed"}}>
        <h2 className="First-Title">Primera conexión API local</h2>
      </div>
      <div>
        <LoadingList isLoading={appStateLoading} contents={appStateObject}/>
      </div>

      <br />
      <form onSubmit={handleSubmit}>
        <input 
        type="text"
        value={name}
        placeholder="Nombre película"
        onChange={(e) => setName(e.target.value)}
        style={{width: "400px"}}
        />
        <input 
        type="number"
        value={year}
        placeholder="Año de publicación"
        onChange={(e) => setYear(e.target.value)}
        />
        <input 
        type="number"
        value={director}
        placeholder="Id del Director"
        onChange={(e) => setDirector(e.target.value)}
        />
        <div>{}</div>
      </form>
    </div>
  );
}

export default App;
