import React from "react";

const List = (props) => {
    const { contents } = props;
    if (!contents || contents.length === 0) return <h1>No hay peliculas</h1>

    return (
        <ul>
        <h2 className="Second-Title">Estas son las peliculas disponibles</h2>
        <table>
        <tr>
            <th>Id:</th>
            <th>Nombre:</th>
            <th>Anno:</th>
            <th>Id del director:</th>
            <th>Nombre:</th>
            <th>Apellido:</th>
        </tr> 
        <tr>
        {contents.map((content) => {
            return (
                 <td>{content.id}</td>
             );
         })}

        </tr>
        <tr>
        {contents.map((content) => {
            return (
                         <td>{content.name}</td>

             );
         })}

        </tr>
        <tr>
        {contents.map((content) => {
            return (
                <td>{content.director_id}</td>

             );
         })}
        </tr>
        <tr>
        {contents.map((content) => {
            return (
                <td>{content.director_first_name}</td>

             );
         })}
        </tr>
        <tr>
        {contents.map((content) => {
            return (
                <td>{content.director_last_name}</td> 
             );
         })}
        </tr>

        </table>


        </ul>
    );

};

export default List;
