import React, {useState, useEffect} from "react";
import List from "./components/List";
import WithLoadingList from "./components/WithLoadingList";
import './App.css';
import {deleteFetch, getFetch, postFetch, putFetch} from "./components/FetchMethods";

function App() {
  const LoadingList = WithLoadingList(List);

  const [appStateLoading, setAppStateLoading] = useState(false)
  const [appStateObject, setAppStateObject] = useState(null)

  const [name, setName] = useState("");
  const [message1, setMenssage1] = useState("");
  const [refresh, setRefresh] = useState(true);

  const [nameEdit, setNameEdit] = useState("");
  const [message2, setMenssage2] = useState("");
  const [idEdit, setEdit] = useState("");

  const [idDelete, setDelete] = useState("");
  const [message3, setMenssage3] = useState("");

  useEffect(() => {
    if(refresh){
      setAppStateLoading(true);
      getFetch("medicines").then(val => setAppStateObject(val))
      setAppStateLoading(false);
      setRefresh(false);
    }
  }, [setAppStateLoading, setAppStateObject, refresh])

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      postFetch("medicines", {name: name }).then(() => {
        setEdit("");
        setName("");
        setMenssage1("Creado correctamente");
        setRefresh(true);
      });
    } catch (err) {
      console.log(err)
    }
  }

  const handleEdit= async (e) => {
    e.preventDefault();
    try {
      putFetch(`medicines/${idEdit}`, {name: nameEdit }).then(() => {
        setNameEdit("");
        setMenssage2("Editado correctamente");
        setRefresh(true);
      });
    } catch (err) {
      console.log(err)
    }
  }

  const handleDelete= async (e) => {
    e.preventDefault();
    try {
      deleteFetch(`medicines/${idDelete}`).then(() =>{
        setDelete("");
        setRefresh(true);
        setMenssage3("Eliminado correctamente");
      })
    } catch (err) {
      console.log(err)
    }
  }

  return (
    <div className="App">
      <div style={{borderStyle: "dashed"}}>
        <h2 className="First-Title">Primera conexion API local</h2>
      </div>
      <div>
        <LoadingList isLoading={appStateLoading} contents={appStateObject} />
      </div>

      <br />
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          value={name}
          placeholder="Nombre del medicamento"
          onChange={(e) => setName(e.target.value)}
          style={{width: "400px"}}
        />
        <div>{message1 ? <p>{message1}</p> : null}</div>
        <button type="submit">Crear</button>
      </form>

      <br />
      <form onSubmit={handleEdit}>
        <input
          type="text"
          value={idEdit}
          placeholder="Indentificador"
          onChange={(e) => setEdit(e.target.value)}
        />
        <input
          type="text"
          value={nameEdit}
          placeholder="Nombre del medicamento"
          onChange={(e) => setNameEdit(e.target.value)}
          style={{width: "400px"}}
        />
        <div>{message2 ? <p>{message2}</p> : null}</div>
        <button type="submit">Editar</button>
      </form>

      <br />
      <form onSubmit={handleDelete}>
        <input
          type="text"
          value={idDelete}
          placeholder="Indentificador"
          onChange={(e) => setDelete(e.target.value)}
        />
        <div>{message3 ? <p>{message3}</p> : null}</div>
        <button type="submit">Eliminar</button>
      </form>
    </div>
  );
}

export default App;
