import React from "react";

const List = (props) => {
    const {contents} = props;

    if (!contents || contents.length === 0) return <h1>No hay peliculas</h1>

    return (
    <ul>
        <h2>Estos son las peliculas disponibles</h2>
        {contents.map((content) => {
            return (
                <textarea class="text">
                    <li key={content.id}>
                        <span>{content.id} <b>Nombre:</b> {content.name} </span>
                        <span><b>AÃ±o de publicacion:</b> {content.publication_year} </span>
                        <span><b>Nombre director:</b> {content.first_name} {content.last_name} </span>
                    </li>
                </textarea>
            )
                
        })}
    </ul>
    );
};
export default List;
