import React, { useState, useEffect } from "react"
import List from "./components/List";
import WithLoadingList from "./components/WithLoadingList";
import './App.css';
import { getFetch, postFetch, putFetch, deleteFetch} from "./components/FetchMethods"


function App() {
  const LoadingList = WithLoadingList(List);

  const [appStateLoading, setAppStateLoading] = useState(false);
  const [appStateObject, setAppStateObject] = useState(null);

  const [name, setName] = useState("");
  const [year, setYear] = useState("");
  const [idDirec, setDirec] = useState("");  
  const [message1, setMessage1] = useState("");
  const [rfs, setRfs] = useState (true);


  useEffect(() => {
    if(rfs){
      setAppStateLoading(true);
      getFetch("movies").then(val => setAppStateObject(val))
      setAppStateLoading(false);
    }
  }, [setAppStateObject, setAppStateLoading])

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      postFetch("movies", { name: name, year: year, director_id: idDirec }).then(() => {
        setName("");
        setYear("");
        setDirec("");
        setMessage1("Creado correctamente");
        setRfs(true);
      });
    } catch (err) {
      console.log(err)
    }
  }

  const handleEdit= async (e) => {
    e.preventDefault();
    try{
      putFetch(`movies/$`)
    }catch(err){
      console.log(err)
    }
  }

  return (
    <div className="App">
      <div style={{ borderStyle: "dashed" }}>
        <h2 className="First-Title">Primera conexi√≥n API local</h2>
      </div>
      <div>
        <LoadingList isLoading={appStateLoading} contents={appStateObject} />
      </div>

      <br/>
      <form onSubmit={handleSubmit}>
        <input 
        type="text"
        value={name}
        placeholder="Nombre del medicamento"
        onChange={(e) => setName(e.target.value)}
        />
        <div>{message1 ?<p>{message1}</p>: null}</div>
        <button type="submit">Crear</button>
      </form>
    </div>
  );
}

export default App;
