import React from 'react';

const List = ({ contents }) => {

  if (!contents || contents.length === 0) return <h1>No hay contenido</h1>
  return (
    <ul >
      <h2>Estos son los contenidos disponibles</h2>
      {contents.map(({ id, name, premiere_date, director: { firstname, lastname } }) => (
        <div style={styles.elementContainer}>
          <li key={`${id}${name}`}>
            <span><b>Id: </b> {id}</span><br />
            <span><b>Nombre: </b> {name}</span><br />
            <span><b>Fecha de estreno: </b> {premiere_date}</span><br />
            <span><b>Nombre del director: </b>{`${firstname} ${lastname}`}</span><br />
          </li>
        </div>
      ))}
    </ul>
  )
}
export default List

const styles = {
  elementContainer: {
    padding: 10,
  }
}
