import React, { useState, useEffect } from 'react';
import List from './components/List'
import WithLoadingList from './components/WithLoadingList'
import './App.css'
import { deleteFetch, getFetch, postFetch, putFetch } from './components/FetchMethods'


function App() {

  const LoadingList = WithLoadingList(List)

  const [content, setContent] = useState([])
  const [loading, setLoading] = useState(false)

  const [name, setName] = useState('')
  const [premiereDate, setPremiereDate] = useState('')
  const [directorId, setDirectorId] = useState('')
  const [refresh, setRefresh] = useState(true)
  const [message1, setMessage1] = useState('')

  const [nameEdit, setNameEdit] = useState('')
  const [idEdit, setIdEdit] = useState('')
  const [message2, setMessage2] = useState('')

  const [idDelete, setIdDelete] = useState('')
  const [message3, setMessage3] = useState('')

  useEffect(() => {
    if (!refresh) return
    setLoading(true)
    getFetch('movies/').then((data) => {
      setContent(data)
      setLoading(false)
    })
    setRefresh(false)
  }, [setContent, setLoading, refresh])

  const handleSubmit = async (event) => {
    event.preventDefault()
    try {
      postFetch('movies/', { name: name, premiere_date: premiereDate, directorId })
        .then((response) => {
          setName('')
          setPremiereDate('')
          setDirectorId('')
          setMessage1('Creado correctamente')
          setRefresh(true)
        })
    } catch (error) {
      console.log(error);
    }
  }

  const handleEdit = async (event) => {
    event.preventDefault()
    try {
      putFetch(`movies/${idEdit}`, { name: nameEdit })
        .then(() => {
          setNameEdit('')
          setIdEdit('')
          setMessage2('Editado correctamente')
          setRefresh(true)
        })
    } catch (error) {
      console.log(error);
    }
  }

  const handleDelete = async (event) => {
    event.preventDefault()
    try {
      deleteFetch(`movies/${idDelete}`)
        .then(() => {
          setIdDelete('')
          setMessage3('Eliminido correctamente')
          setRefresh(true)
        })
    } catch (error) {
      console.log(error);
    }
  }


  return (
    <div className='container'>
      <h2 className='header'> Primera conexion API Local</h2>
      <LoadingList isLoading={loading} contents={content} />
      <form onSubmit={handleSubmit}>
        <div>
          <input
            id='name'
            type='text'
            value={name}
            placeholder='nombre del medicamento'
            onChange={(e) => { setName(e.target.value); }} />
        </div>
        <div>{message1 && <p>{message1}</p>}</div>
        <button type='submit'>Crear medicamento</button>
      </form>
      <br />
      <form onSubmit={handleEdit}>
        <div>
          <input
            id='id'
            type='text'
            value={idEdit}
            placeholder='identificador'
            onChange={(e) => { setIdEdit(e.target.value); }} />
        </div>
        <div>
          <input
            id='id'
            type='text'
            value={nameEdit}
            placeholder='nombre del medicamento'
            onChange={(e) => { setNameEdit(e.target.value); }} />
        </div>
        <div>{message2 && <p>{message2}</p>}</div>
        <button type='submit'>Editar medicamento</button>
      </form>
      <form onSubmit={handleDelete}>
        <div>
          <input
            id='id'
            type='text'
            value={idDelete}
            placeholder='identificador'
            onChange={(e) => { setIdDelete(e.target.value); }} />
        </div>
        <div>{message3 && <p>{message3}</p>}</div>
        <button type='submit'>Eliminar medicamento</button>
      </form>
    </div>
  );
}




export default App;
