import React from "react";

const List = (props)=>{
    const{contents} = props;
    if (!contents || contents.length === 0)return <h1>No hay Contenidos</h1>
    return (
        <ul>
            <h2 className="List-Component">Estos son los contenidos disponibles</h2>
            {contents.map((content)=> {
                return (
                    <div style={{background: "yellow", margin: "10px"}}>
                        <li key={content.id}>
                            <span className="List-Element">{content.id}<b>ID: </b> {content.id}</span>
                            <span className="List-Element">{content.id}<b>Nombre: </b> {content.name}</span>
                            <span className="List-Element">{content.id}<b>Year: </b> {content.year}</span>
                            <span className="List-Element">{content.id}<b>Director: </b> {content.director.first_name}</span>
                        </li>
                    </div>

                );
            })}
        </ul>
    );
}
export default List;
